mapper.select:
SELECT nickname 
FROM sh_user 
WHERE user_key = #{USER_KEY}

SELECT team_name, title, board_num, writer, regdate, hit, reply_count, board_content, uploadFile 
FROM community 
WHERE board_num = #{BOARD_NUM}

SELECT count(*)
FROM cm_comment
WHERE board_num = #{BOARD_NUM, jdbcType=NUMERIC}

mapper.update:
UPDATE community 
SET team_name = #{TEAM_NAME}, title = #{TITLE}, board_content = #{BOARD_CONTENT}, moddate = sysdate
WHERE board_num = #{BOARD_NUM, jdbcType=NUMERIC}
