mapper.select:
		
		    SELECT rownum rn, rv_num, title, re_date, re_hit
     		FROM	
				( SELECT rv_num, title, re_date, re_hit
			  	  FROM review
			   	  WHERE user_key = #{key}
			  		AND rv_num > (#{pagingVO.pageNum} - 1) * #{pagingVO.amount}
       		  	  ORDER BY rv_num desc )
     		 WHERE rv_num <= #{pagingVO.pageNum} * #{pagingVO.amount}

mapper.update:
UPDATE shop_order 
SET stts = 4 
WHERE order_num = #{order_num}

UPDATE address
SET
	name = #{name},
	addr = #{addr},
	contact = #{contact}
WHERE user_key = #{user_key}
