mapper.select:
	
	SELECT order_num, or_date, items, pymntamnt 
	FROM shop_order
	WHERE name = (SELECT name FROM sh_user WHERE user_key = #{key} )
		AND order_num <= #{pagingVO.pageNum} * #{pagingVO.amount}
		AND order_num > (#{pagingVO.pageNum} - 1) * #{pagingVO.amount}
	ORDER BY order_num desc
